// frontend/src/pages/AuthCallback.jsx
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '@/lib/supabase';

const AuthCallback = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Supabase automatically parses the URL hash when this component loads
    supabase.auth.onAuthStateChange((event, session) => {
      if (session) {
        // Successful login -> Go to Dashboard
        navigate('/dashboard');
      } else {
        // No session found? Go back to login
        // Optional: specific check if hash exists but failed
        if (!window.location.hash) {
             navigate('/login');
        }
      }
    });
  }, [navigate]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-slate-50 dark:bg-slate-900">
      <div className="w-10 h-10 border-4 border-violet-600 border-t-transparent rounded-full animate-spin mb-4" />
      <h2 className="text-xl font-semibold">Verifying secure login...</h2>
    </div>
  );
};

export default AuthCallback;
