import React, { useState, useEffect } from 'react';
import { useSearchParams, useParams } from 'react-router-dom'; 
import { supabase } from '@/lib/supabase';
import { Star } from 'lucide-react';
import '@/index.css'; // ✅ REQUIRED: Ensures Tailwind styles load correctly in the iframe

const WallOfLove = () => {
  const { slug } = useParams(); 
  const [searchParams] = useSearchParams(); 

  const theme = searchParams.get('theme') || 'light';
  const layout = searchParams.get('layout') || 'grid';

  const [testimonials, setTestimonials] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (slug) {
      fetchTestimonials();
    }
  }, [slug]);

  const fetchTestimonials = async () => {
    try {
      const { data: spaceData, error: spaceError } = await supabase
        .from('spaces')
        .select('id')
        .eq('slug', slug)
        .single();

      if (spaceError) throw spaceError;

      const { data, error } = await supabase
        .from('testimonials')
        .select('*')
        .eq('space_id', spaceData.id)
        .eq('is_liked', true)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTestimonials(data || []);
    } catch (error) {
      console.error('Error fetching testimonials:', error);
    } finally {
      setLoading(false);
    }
  };

  // ✅ CSS Classes for Full-Screen Consistency
  // These ensure the iframe background matches the theme perfectly
  // Added 'min-h-screen' so it fills the iframe height
  const wrapperClasses = `min-h-screen p-6 font-sans antialiased ${
    theme === 'dark' ? 'bg-gray-900 text-gray-200' : 'bg-gray-50 text-gray-800'
  }`;

  if (loading) {
    return (
      <div className={wrapperClasses}>
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {[1, 2, 3].map((i) => (
            <div 
              key={i}
              className={`animate-pulse p-6 rounded-xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'}`}
            >
              <div className="h-4 bg-gray-300 rounded w-3/4 mb-4"></div>
              <div className="h-4 bg-gray-300 rounded w-full mb-2"></div>
              <div className="h-4 bg-gray-300 rounded w-5/6"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  if (testimonials.length === 0) {
    return (
      <div className={`${wrapperClasses} flex items-center justify-center`}>
        <p className="text-lg opacity-60">No testimonials yet</p>
      </div>
    );
  }

  return (
    <div className={wrapperClasses}>
      <div className={`grid gap-4 ${
        layout === 'carousel' 
          ? 'grid-flow-col auto-cols-[300px] overflow-x-auto pb-4 snap-x' 
          : layout === 'masonry'
          ? 'columns-1 md:columns-2 lg:columns-3 space-y-4'
          : 'md:grid-cols-2 lg:grid-cols-3'
      }`}>
        {testimonials.map((testimonial) => (
          <div
            key={testimonial.id}
            className={`p-6 rounded-xl shadow-sm transition-all hover:shadow-md break-inside-avoid ${
              theme === 'dark' ? 'bg-gray-800 border border-gray-700' : 'bg-white border border-gray-100'
            } ${layout === 'masonry' ? 'mb-4' : ''} ${layout === 'carousel' ? 'snap-center' : ''}`}
          >
            {/* Rating */}
            {testimonial.stars && ( 
              <div className="flex gap-0.5 mb-3">
                {[...Array(5)].map((_, i) => (
                  <Star
                    key={i}
                    className={`w-4 h-4 ${
                      i < testimonial.stars
                        ? 'fill-yellow-400 text-yellow-400'
                        : 'text-gray-300'
                    }`}
                  />
                ))}
              </div>
            )}

            {/* Content */}
            {testimonial.type === 'video' && testimonial.video_url ? (
              <video
                src={testimonial.video_url}
                controls
                className="w-full rounded-lg mb-4 aspect-video object-cover"
              />
            ) : (
              <p className={`text-sm mb-4 leading-relaxed ${theme === 'dark' ? 'text-gray-300' : 'text-gray-600'}`}>
                "{testimonial.content}"
              </p>
            )}

            {/* Author */}
            <div className="flex items-center gap-3 pt-2 border-t border-gray-100 dark:border-gray-700/50">
              <div className={`w-9 h-9 rounded-full flex items-center justify-center text-sm font-bold ${
                theme === 'dark' 
                  ? 'bg-violet-900/50 text-violet-300' 
                  : 'bg-violet-100 text-violet-600'
              }`}>
                {testimonial.name?.charAt(0).toUpperCase() || "A"}
              </div>
              <div>
                <div className={`font-medium text-sm ${
                  theme === 'dark' ? 'text-white' : 'text-gray-900'
                }`}>
                  {testimonial.name || "Anonymous"}
                </div>
                {testimonial.company && (
                    <div className={`text-xs ${theme === 'dark' ? 'text-gray-400' : 'text-gray-500'}`}>
                        {testimonial.company}
                    </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WallOfLove;
