import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, Camera, Mail, User, Loader2, AlertCircle } from 'lucide-react';
import { supabase } from '@/lib/supabase';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';

const ProfileModal = ({ isOpen, onClose, user, profile, onProfileUpdate }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(false);

  // Form Data
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [avatarUrl, setAvatarUrl] = useState('');
  const [avatarFile, setAvatarFile] = useState(null);

  // Error States
  const [emailError, setEmailError] = useState(''); // New state for inline error

  // Email Change Logic
  const [isEmailChangePending, setIsEmailChangePending] = useState(false);
  const [otp, setOtp] = useState('');
  const [newEmailToVerify, setNewEmailToVerify] = useState('');

  const fileInputRef = useRef(null);
  const { toast } = useToast();

  // Reset state when modal opens
  useEffect(() => {
    if (isOpen) {
        setFullName(profile?.full_name || '');
        setEmail(user?.email || '');
        setAvatarUrl(profile?.avatar_url || '');
        setAvatarFile(null);
        setIsEditing(false);
        setIsEmailChangePending(false);
        setOtp('');
        setEmailError(''); // Clear errors
    }
  }, [isOpen, profile, user]);

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAvatarFile(file);
      setAvatarUrl(URL.createObjectURL(file)); // Preview
    }
  };

  const handleSaveChanges = async () => {
    setLoading(true);
    setEmailError(''); 

    try {
      // --- PART 1: SAVE PROFILE DATA (Name & Photo) ---
      let uploadedAvatarUrl = avatarUrl;

      // 1. Upload Photo (Protected)
      if (avatarFile) {
        const fileExt = avatarFile.name.split('.').pop();
        const fileName = `${user.id}-${Math.random()}.${fileExt}`;
        const filePath = `${fileName}`;

        try {
            const { error: uploadError } = await supabase.storage
            .from('avatars')
            .upload(filePath, avatarFile);

            if (uploadError) throw uploadError;
        } catch (uploadError) {
             const isStreamError = uploadError.message && (
                uploadError.message.includes('body stream') || 
                uploadError.message.includes('Failed to execute')
            );
            if (!isStreamError) throw uploadError;
        }

        const { data: { publicUrl } } = supabase.storage
          .from('avatars')
          .getPublicUrl(filePath);

        uploadedAvatarUrl = publicUrl;
      }

      // 2. Update Public Profile (Protected)
      const updates = {
        id: user.id,
        full_name: fullName,
        avatar_url: uploadedAvatarUrl,
        updated_at: new Date(),
      };

      try {
        const { error: profileError } = await supabase
          .from('profiles')
          .upsert(updates)
          .select();

        if (profileError) throw profileError;

      } catch (upsertError) {
        const isStreamError = upsertError.message && (
            upsertError.message.includes('body stream') || 
            upsertError.message.includes('Failed to execute')
        );
        if (!isStreamError) throw upsertError;
      }

      // --- PART 2: HANDLE EMAIL CHANGE (NOW PROTECTED) ---
      if (email !== user.email) {

        let emailErrorObj = null;

        try {
            // We wrap this in a try/catch to catch the "Stream" crash
            const { error } = await supabase.auth.updateUser({ email: email });
            emailErrorObj = error;
        } catch (crashError) {
            // CHECK: Is this the specific Codespaces crash?
            const isStreamError = crashError.message && (
                crashError.message.includes('body stream') || 
                crashError.message.includes('json') ||
                crashError.message.includes('Failed to execute')
            );

            // If it's NOT the stream error, it's a real crash. Throw it.
            if (!isStreamError) throw crashError;

            // If it IS the stream error, we ignore it and assume the email was sent.
            console.warn("Supabase stream error ignored for email update.");
        }

        // Handle Logic Errors (like Duplicate Email)
        if (emailErrorObj) {
            if (emailErrorObj.message.includes("already registered") || emailErrorObj.status === 422) {
                setEmailError("This email is already registered.");
                setLoading(false);
                return;
            }
            throw emailErrorObj;
        }

        // Success: Switch to OTP View
        setNewEmailToVerify(email);
        setIsEmailChangePending(true);
        toast({
          title: "Verification code sent",
          description: `Please enter the code sent to ${email}`,
        });
        setLoading(false);
        return; 
      }

      // If no email change, we are done
      toast({ title: "Profile updated successfully!" });
      if (onProfileUpdate) await onProfileUpdate();
      setIsEditing(false);
      onClose();

    } catch (error) {
      console.error(error);
      toast({
        title: "Error updating profile",
        description: error.message,
        variant: "destructive"
      });
    } finally {
      if (!isEmailChangePending) setLoading(false);
    }
  };

  const handleVerifyEmailOtp = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      // Verify using type: 'email_change'
      const { error } = await supabase.auth.verifyOtp({
        email: newEmailToVerify,
        token: otp,
        type: 'email_change'
      });

      if (error) throw error;

      toast({ title: "Email updated successfully!" });
      if (onProfileUpdate) await onProfileUpdate();
      setIsEmailChangePending(false);
      setIsEditing(false);
      onClose();
    } catch (error) {
      toast({
        title: "Verification failed",
        description: "Invalid code. Please try again.",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <AnimatePresence>
      <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm">
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.95 }}
          className="w-full max-w-md bg-white dark:bg-gray-900 rounded-2xl shadow-xl overflow-hidden border border-slate-200 dark:border-slate-800"
        >
          {/* Header */}
          <div className="relative h-32 bg-gradient-to-r from-violet-600 to-indigo-600">
            <button 
              onClick={onClose}
              className="absolute top-4 right-4 p-2 bg-white/20 hover:bg-white/30 rounded-full text-white transition-colors"
            >
              <X className="w-5 h-5" />
            </button>
            <div className="absolute -bottom-12 left-1/2 -translate-x-1/2">
              <div className="relative group">
                <div className="w-24 h-24 rounded-full border-4 border-white dark:border-gray-900 overflow-hidden bg-slate-100">
                   {avatarUrl ? (
                     <img src={avatarUrl} alt="Profile" className="w-full h-full object-cover" />
                   ) : (
                     <div className="w-full h-full flex items-center justify-center bg-violet-100 text-violet-600">
                        <User className="w-10 h-10" />
                     </div>
                   )}
                </div>
                {isEditing && !isEmailChangePending && (
                  <button 
                    onClick={() => fileInputRef.current?.click()}
                    className="absolute bottom-0 right-0 p-2 bg-violet-600 text-white rounded-full shadow-lg hover:bg-violet-700"
                  >
                    <Camera className="w-4 h-4" />
                  </button>
                )}
                <input 
                  type="file" 
                  ref={fileInputRef} 
                  className="hidden" 
                  accept="image/*"
                  onChange={handleFileSelect}
                />
              </div>
            </div>
          </div>

          {/* Content */}
          <div className="pt-16 pb-8 px-8">
            {isEmailChangePending ? (
               /* OTP Form (Same style as Sign Up) */
               <div className="space-y-6 text-center">
                  <div className="flex justify-center mb-4">
                    <div className="bg-green-100 p-3 rounded-full">
                        <Mail className="w-6 h-6 text-green-600" />
                    </div>
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold">Verify New Email</h3>
                    <p className="text-sm text-muted-foreground mt-1">
                      Enter the 6-digit code sent to <span className="font-medium text-slate-900 dark:text-white">{newEmailToVerify}</span>
                    </p>
                  </div>

                  {/* OTP Input - Styled Cleanly */}
                  <Input 
                    placeholder="Enter code"
                    className="text-center text-xl tracking-widest h-12"
                    maxLength={6}
                    value={otp} 
                    onChange={(e) => setOtp(e.target.value)}
                  />

                  <Button 
                    onClick={handleVerifyEmailOtp} 
                    className="w-full bg-violet-600 hover:bg-violet-700 text-white h-11"
                    disabled={loading || otp.length < 6}
                  >
                    {loading && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
                    Confirm Change
                  </Button>
               </div>
            ) : (
               /* Profile Edit Form */
               <div className="space-y-6">
                {!isEditing ? (
                  <div className="text-center space-y-4">
                    <div>
                      <h2 className="text-2xl font-bold text-slate-900 dark:text-white">{fullName || 'User'}</h2>
                      <p className="text-slate-500 dark:text-slate-400">{email}</p>
                    </div>
                    <Button variant="outline" onClick={() => setIsEditing(true)} className="w-full">
                      Edit Profile
                    </Button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    <div className="space-y-2">
                      <Label>Full Name</Label>
                      <Input value={fullName} onChange={(e) => setFullName(e.target.value)} />
                    </div>
                    <div className="space-y-2">
                      <Label>Email Address</Label>
                      <Input 
                        value={email} 
                        onChange={(e) => {
                            setEmail(e.target.value);
                            setEmailError(''); // Clear error when user types
                        }}
                        className={emailError ? "border-red-500 focus-visible:ring-red-500" : ""}
                      />

                      {/* INLINE ERROR MESSAGE */}
                      {emailError && (
                          <p className="text-sm text-red-600 flex items-center gap-1 mt-1">
                              <AlertCircle className="w-3 h-3" /> {emailError}
                          </p>
                      )}

                      {!emailError && (
                        <p className="text-xs text-muted-foreground flex items-center gap-1">
                            <AlertCircle className="w-3 h-3" /> 
                            Changing email requires verification.
                        </p>
                      )}
                    </div>
                    <div className="flex gap-3 pt-2">
                       <Button variant="ghost" className="flex-1" onClick={() => setIsEditing(false)}>Cancel</Button>
                       <Button className="flex-1 bg-violet-600 hover:bg-violet-700 text-white" onClick={handleSaveChanges} disabled={loading}>
                          {loading ? <Loader2 className="w-4 h-4 animate-spin" /> : 'Save Changes'}
                       </Button>
                    </div>
                  </div>
                )}
               </div>
            )}
          </div>
        </motion.div>
      </div>
    </AnimatePresence>
  );
};

export default ProfileModal;
