import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Plus, 
  LayoutGrid, 
  MessageSquare, 
  Video, 
  Loader2, 
  Trash2, 
  ExternalLink, 
  BarChart3,
  User,
  LogOut,
  Settings
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription, CardFooter } from '@/components/ui/card';
import { useAuth } from '@/contexts/AuthContext';
import { supabase } from '@/lib/supabase';
import { useToast } from '@/hooks/use-toast';
import ProfileModal from '@/components/ProfileModal';

const Dashboard = () => {
  const { user, profile, signOut, refreshProfile } = useAuth();
  const navigate = useNavigate();
  const { toast } = useToast();

  // State
  const [spaces, setSpaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isProfileModalOpen, setIsProfileModalOpen] = useState(false);

  // Fetch Spaces
  useEffect(() => {
    const fetchSpaces = async () => {
      try {
        if (!user) return;

        const { data, error } = await supabase
          .from('spaces')
          .select('*')
          .eq('user_id', user.id)
          .order('created_at', { ascending: false });

        if (error) throw error;
        setSpaces(data || []);
      } catch (error) {
        console.error('Error fetching spaces:', error);
        toast({
          title: "Error loading spaces",
          description: error.message,
          variant: "destructive"
        });
      } finally {
        setLoading(false);
      }
    };

    fetchSpaces();
  }, [user, toast]);

  const handleSignOut = async () => {
    try {
      await signOut();
      navigate('/');
    } catch (error) {
      toast({
        title: "Error signing out",
        description: error.message,
        variant: "destructive"
      });
    }
  };

  const handleDeleteSpace = async (spaceId, e) => {
    e.preventDefault(); // Prevent navigation
    e.stopPropagation();

    if (!window.confirm("Are you sure you want to delete this space? This cannot be undone.")) return;

    try {
      const { error } = await supabase
        .from('spaces')
        .delete()
        .eq('id', spaceId);

      if (error) throw error;

      setSpaces(spaces.filter(space => space.id !== spaceId));
      toast({
        title: "Space deleted",
        description: "The space has been permanently removed.",
      });
    } catch (error) {
      toast({
        title: "Error deleting space",
        description: error.message,
        variant: "destructive"
      });
    }
  };

  // Animation variants
  const container = {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: { staggerChildren: 0.1 }
    }
  };

  const item = {
    hidden: { opacity: 0, y: 20 },
    show: { opacity: 1, y: 0 }
  };

  return (
    <div className="min-h-screen bg-slate-50 dark:bg-slate-900">
      {/* --- Navigation Bar --- */}
      <nav className="bg-white dark:bg-slate-800 border-b border-slate-200 dark:border-slate-700 sticky top-0 z-30">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center gap-2">
              <div className="w-8 h-8 bg-gradient-to-br from-violet-600 to-indigo-600 rounded-lg flex items-center justify-center shadow-lg shadow-violet-500/20">
                <LayoutGrid className="w-5 h-5 text-white" />
              </div>
              <span className="font-bold text-xl bg-gradient-to-r from-violet-600 to-indigo-600 bg-clip-text text-transparent">
                TrustFlow
              </span>
            </div>

            <div className="flex items-center gap-4">
              <Link to="/create-space">
                <Button className="hidden sm:flex bg-violet-600 hover:bg-violet-700 text-white shadow-lg shadow-violet-500/20">
                  <Plus className="w-4 h-4 mr-2" />
                  New Space
                </Button>
              </Link>

              {/* --- PROFILE DROPDOWN --- */}
              <div className="relative">
                <button 
                  onClick={() => setIsProfileOpen(!isProfileOpen)}
                  className="w-10 h-10 rounded-full border-2 border-violet-100 dark:border-slate-700 overflow-hidden hover:ring-2 hover:ring-violet-500 transition-all focus:outline-none focus:ring-2 focus:ring-violet-500"
                >
                  {profile?.avatar_url ? (
                    <img src={profile.avatar_url} alt="Profile" className="w-full h-full object-cover" />
                  ) : (
                    <div className="w-full h-full bg-violet-100 dark:bg-violet-900/30 flex items-center justify-center text-violet-600">
                      <User className="w-5 h-5" />
                    </div>
                  )}
                </button>

                <AnimatePresence>
                  {isProfileOpen && (
                    <>
                      {/* Click outside backdrop */}
                      <div 
                        className="fixed inset-0 z-10 cursor-default" 
                        onClick={() => setIsProfileOpen(false)} 
                      />

                      {/* Dropdown Menu */}
                      <motion.div
                        initial={{ opacity: 0, y: 10, scale: 0.95 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, y: 10, scale: 0.95 }}
                        transition={{ duration: 0.1 }}
                        className="absolute right-0 top-12 w-64 bg-white dark:bg-slate-800 rounded-xl shadow-xl border border-slate-200 dark:border-slate-700 z-20 overflow-hidden"
                      >
                        <div className="p-4 border-b border-slate-100 dark:border-slate-700 bg-slate-50/50 dark:bg-slate-800/50">
                          <p className="text-sm font-semibold text-slate-900 dark:text-slate-100 truncate">
                            {profile?.full_name || 'User'}
                          </p>
                          <p className="text-xs text-slate-500 dark:text-slate-400 truncate mt-0.5">
                            {user?.email}
                          </p>
                        </div>

                        <div className="p-1.5">
                          <button 
                            onClick={() => {
                              setIsProfileOpen(false);
                              setIsProfileModalOpen(true);
                            }}
                            className="w-full flex items-center gap-2.5 px-3 py-2.5 text-sm font-medium text-slate-700 dark:text-slate-200 hover:bg-violet-50 dark:hover:bg-violet-900/20 hover:text-violet-700 dark:hover:text-violet-300 rounded-lg transition-colors group"
                          >
                            <Settings className="w-4 h-4 text-slate-400 group-hover:text-violet-600 transition-colors" />
                            Profile Settings
                          </button>

                          <button 
                            onClick={handleSignOut}
                            className="w-full flex items-center gap-2.5 px-3 py-2.5 text-sm font-medium text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20 rounded-lg transition-colors mt-1 group"
                          >
                            <LogOut className="w-4 h-4 text-red-400 group-hover:text-red-600 transition-colors" />
                            Sign Out
                          </button>
                        </div>
                      </motion.div>
                    </>
                  )}
                </AnimatePresence>
              </div>
            </div>
          </div>
        </div>
      </nav>

      {/* --- Main Content --- */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4 mb-8">
          <div>
            <h1 className="text-2xl font-bold text-slate-900 dark:text-slate-100">Your Spaces</h1>
            <p className="text-slate-500 dark:text-slate-400 mt-1">Manage your testimonial collections</p>
          </div>
          <Link to="/create-space" className="sm:hidden">
            <Button className="w-full bg-violet-600 hover:bg-violet-700 text-white">
              <Plus className="w-4 h-4 mr-2" />
              New Space
            </Button>
          </Link>
        </div>

        {loading ? (
          <div className="flex items-center justify-center h-64">
            <Loader2 className="w-8 h-8 text-violet-600 animate-spin" />
          </div>
        ) : spaces.length === 0 ? (
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="text-center py-20 px-4 bg-white dark:bg-slate-800 rounded-2xl border border-dashed border-slate-300 dark:border-slate-700"
          >
            <div className="w-16 h-16 bg-violet-100 dark:bg-violet-900/30 text-violet-600 rounded-2xl flex items-center justify-center mx-auto mb-4">
              <LayoutGrid className="w-8 h-8" />
            </div>
            <h3 className="text-lg font-semibold text-slate-900 dark:text-slate-100 mb-2">No spaces yet</h3>
            <p className="text-slate-500 dark:text-slate-400 max-w-sm mx-auto mb-6">
              Create your first space to start collecting testimonials from your customers.
            </p>
            <Link to="/create-space">
              <Button className="bg-violet-600 hover:bg-violet-700 text-white">
                <Plus className="w-4 h-4 mr-2" />
                Create Space
              </Button>
            </Link>
          </motion.div>
        ) : (
          <motion.div 
            variants={container}
            initial="hidden"
            animate="show"
            className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6"
          >
            {spaces.map((space) => (
              <motion.div key={space.id} variants={item}>
                <Link to={`/dashboard/${space.id}`}>
                  <Card className="h-full hover:shadow-lg transition-all hover:-translate-y-1 border-slate-200 dark:border-slate-700 group cursor-pointer">
                    <CardHeader className="space-y-1">
                      <div className="flex items-start justify-between">
                        <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-violet-100 to-indigo-100 dark:from-violet-900/30 dark:to-indigo-900/30 flex items-center justify-center text-violet-600 dark:text-violet-400 mb-2">
                          <MessageSquare className="w-5 h-5" />
                        </div>
                        <button 
                          onClick={(e) => handleDeleteSpace(space.id, e)}
                          className="text-slate-400 hover:text-red-500 p-1 opacity-0 group-hover:opacity-100 transition-opacity"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                      <CardTitle className="line-clamp-1">{space.space_name}</CardTitle>
                      <CardDescription className="line-clamp-1">
                        {space.header_title}
                      </CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="flex items-center gap-4 text-sm text-slate-500 dark:text-slate-400">
                        <div className="flex items-center gap-1">
                          <Video className="w-4 h-4" />
                          <span>Video</span>
                        </div>
                        <div className="flex items-center gap-1">
                          <MessageSquare className="w-4 h-4" />
                          <span>Text</span>
                        </div>
                      </div>
                    </CardContent>
                    <CardFooter className="border-t pt-4 text-xs text-slate-500 flex justify-between items-center bg-slate-50/50 dark:bg-slate-800/50">
                      <span className="flex items-center gap-1">
                        <BarChart3 className="w-3 h-3" />
                        View Dashboard
                      </span>
                      <ExternalLink className="w-3 h-3 opacity-50" />
                    </CardFooter>
                  </Card>
                </Link>
              </motion.div>
            ))}
          </motion.div>
        )}
      </main>

      {/* --- Profile Modal (Hidden until toggled) --- */}
      <ProfileModal 
        isOpen={isProfileModalOpen}
        onClose={() => setIsProfileModalOpen(false)}
        user={user}
        profile={profile}
        onProfileUpdate={refreshProfile}
      />
    </div>
  );
};

export default Dashboard;
